query:
query FundamentalsGlobalSTD ($InstrumentList: [OrganizationReferenceIdentifierArgs!]) {
  OrganizationReference(identifiers: $InstrumentList) {
    ObjectId
    ReportedPeriod {
      ObjectId
      FinancialPeriod {
        AnnualReportedFlag
        CumulativeTypeCode
        EarliestAnnounceDateUtcOffsetDuration
        EarliestAnnouncementDate
        FiscalEndMonth
        FiscalYear
        OrganizationId
        PeriodDuration
        PeriodEndDate
        PeriodId
        PeriodStartDate
        PeriodType
        TransitionalFlag
        InterimPeriods {
          InterimNumber
          InterimPeriodId
        }
        AnnualPeriods {
          AnnualPeriodId
          InterimNumber
        }
      }
      FundamentalFxRates {
        FxRates {
          AverageFlag
          ConversionValue
          FromCurrencyId
          PeriodId
          ToCurrencyId
        }
      }
    }
    FiledSource {
      ObjectId
      FinancialSource {
        CapitalChangeAdjustmentDate
        ContainsRestatementFlag
        CumulativeAdjustmentFactor
        DocumentFormat
        DocumentId
        DocumentNumber
        EstimatedFilingDateFlag
        FilingDate
        FilingDateTimeUtcOffset
        OrganizationId
        PreliminaryFlag
        SourceId
        SourceTypeCode
        StatementDate
        ThirdPartySourceCode
        ThirdPartySourcePriority
        Auditors {
          AuditorId
          OpinionCode
          OpinionOnGoingConcernCode
          OpinionOnInternalControlsCode
          PlayingAuditorRoleFlag
          PlayingCsrAuditorRoleFlag
          PlayingTaxAdvisorRoleFlag
        }
      }
    }
  }
}
