query Lookuptables($ValueDomainId: [Long!]) {
  ValueDomainEnumeration(filter: {ValueDomainEnumeration: {ValueDomainId: {IN: $ValueDomainId}}}) {
    ValueDomainEnumeration {
      LongName
      Name
      Value
      ValueDomainEnumerationId
      ValueDomainId
      Names(filter: {LanguageId: {EQ: "505074"}}) {
        LanguageId
        Name
        NameOrder
        NameSequence
        NameType
      }
    }
  }
}
